import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for profile button and click it
    await page.waitForSelector('button.profile-btn', { state: 'visible' });
    await page.click('button.profile-btn');

    // Wait for dropdown to appear and take screenshot
    await page.waitForSelector('#profileDropdown', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_open.png'));

    // Find and verify Profile link
    const profileLink = await page.$('a[href="profile.html"]');
    if (!profileLink) {
      throw new Error('Profile link not found');
    }

    // Verify href attribute
    const href = await profileLink.getAttribute('href');
    if (href !== 'profile.html') {
      throw new Error(`Profile link href is incorrect: ${href}`);
    }

    // Verify link text
    const text = await profileLink.textContent();
    if (text.trim() !== 'Profile') {
      throw new Error(`Profile link text is incorrect: ${text}`);
    }

    // Take final verification screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_verification.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_verification.png'));

    return {
      success: true,
      action: "Successfully opened profile dropdown and verified Profile link href and text",
      extractedData: {
        profileHref: href,
        profileText: text.trim()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
