import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click profile button
    await page.waitForSelector('.profile-btn');
    await page.click('.profile-btn');

    // Wait for dropdown to appear and take screenshot
    await page.waitForSelector('#profileDropdown', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_open.png'));

    // Extract profile link href attribute
    const profileHref = await page.$eval('a[href="profile.html"]', el => el.href);
    const extractedData = { profileHref };

    // Verify the href attribute
    const isValid = profileHref.endsWith('profile.html');

    return {
      success: isValid,
      action: "Opened profile dropdown and verified Profile link href",
      extractedData,
      screenshots,
      error: isValid ? null : 'Profile link href is incorrect'
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('domcontentloaded');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for profile button and click it
    const profileButton = await page.waitForSelector('button.profile-btn', { timeout: 5000 });
    await profileButton.click();

    // Wait for dropdown to appear and take screenshot
    await page.waitForSelector('#profileDropdown', { state: 'visible', timeout: 3000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_open.png'));

    // Find Profile link and verify href attribute
    const profileLink = await page.waitForSelector('a[href="profile.html"]', { timeout: 3000 });
    const hrefValue = await profileLink.getAttribute('href');

    // Verify the href attribute
    if (hrefValue !== 'profile.html') {
      throw new Error(`Profile link href is incorrect. Expected: profile.html, Got: ${hrefValue}`);
    }

    // Take final verification screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_verification.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_verification.png'));

    return {
      success: true,
      action: "Successfully clicked profile button, opened dropdown, and verified Profile link href",
      extractedData: {
        profileHref: hrefValue,
        dropdownVisible: true,
        profileLinkExists: true
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete profile dropdown verification",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('domcontentloaded');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for profile button and click it
    await page.waitForSelector('.profile-btn', { timeout: 5000 });
    await page.click('.profile-btn');

    // Wait for dropdown to appear and take screenshot
    await page.waitForSelector('#profileDropdown', { state: 'visible', timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_open.png'));

    // Verify Profile link exists with correct href
    const profileLink = await page.$('a[href="profile.html"]');
    if (!profileLink) {
      throw new Error('Profile link not found');
    }

    // Verify href attribute
    const href = await profileLink.getAttribute('href');
    if (href !== 'profile.html') {
      throw new Error(`Profile link href is incorrect: ${href}`);
    }

    // Take final verification screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_verified.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_verified.png'));

    return {
      success: true,
      action: "Successfully clicked profile button, opened dropdown, and verified Profile link href",
      extractedData: {
        profileHref: href
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete profile dropdown verification",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
