import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click profile button
    await page.waitForSelector('.profile-btn');
    await page.click('.profile-btn');

    // Wait for dropdown to become visible
    await page.waitForSelector('#profileDropdown', { state: 'visible' });

    // Take screenshot after opening dropdown
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_open.png'));

    // Extract profile link data
    const profileLink = await page.$eval('a[href="profile.html"]', el => ({
      href: el.href,
      text: el.textContent.trim(),
      visible: el.offsetParent !== null
    }));

    // Verify profile link has correct href
    const isHrefCorrect = profileLink.href.endsWith('profile.html');

    return {
      success: isHrefCorrect,
      action: "Clicked profile button to open dropdown and verified Profile link href",
      extractedData: {
        profileLink,
        isHrefCorrect
      },
      screenshots,
      error: isHrefCorrect ? null : 'Profile link href is incorrect'
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for profile button and click it
    await page.waitForSelector('.profile-btn', { state: 'visible' });
    await page.click('.profile-btn');

    // Wait for dropdown to appear
    await page.waitForSelector('#profileDropdown', { state: 'visible' });

    // Take screenshot after opening dropdown
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_open.png'));

    // Verify Profile link exists with correct href
    const profileLink = await page.$("a[href='profile.html']");
    if (!profileLink) {
      throw new Error('Profile link not found');
    }

    // Extract and verify href attribute
    const href = await profileLink.getAttribute('href');
    if (href !== 'profile.html') {
      throw new Error(`Profile link href is incorrect: ${href}`);
    }

    // Extract link text for verification
    const linkText = await profileLink.textContent();

    return {
      success: true,
      action: "Clicked profile button to open dropdown and verified Profile link href",
      extractedData: {
        profileLinkHref: href,
        profileLinkText: linkText.trim()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the profile button using multiple selector strategies
    const profileBtn = await page.waitForSelector('button.profile-btn, button:has-text("J"), [class*="profile"] button');
    await profileBtn.click();

    // Wait for dropdown to appear and take screenshot
    await page.waitForSelector('#profileDropdown', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_open.png'));

    // Verify Profile link has correct href
    const profileLink = await page.waitForSelector('#profileDropdown a:has-text("Profile"), [href="profile.html"]');
    const href = await profileLink.getAttribute('href');

    if (href !== 'profile.html') {
      throw new Error(`Profile link href is incorrect. Expected: profile.html, Got: ${href}`);
    }

    // Take final verification screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_verification.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_verification.png'));

    return {
      success: true,
      action: "Successfully clicked profile button, opened dropdown, and verified Profile link href",
      extractedData: {
        profileHref: href
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
