import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Interact with the search input field
    await page.waitForSelector('#locationInput');
    await page.fill('#locationInput', 'New York'); // Example search term
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_filled_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_filled_search.png'));

    // Trigger search
    const searchBtn = await page.waitForSelector('.search-btn');
    await searchBtn.click();
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_triggered.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_triggered.png'));

    // Wait for localStorage to update
    await page.waitForTimeout(2000); // Wait for a bit to ensure localStorage updates

    // Extract the latest search term from localStorage
    const latestSearchTerm = await page.evaluate(() => {
      return localStorage.getItem('searchHistory'); // Assuming 'searchHistory' is the key used
    });

    return {
      success: true,
      action: "Performed search and extracted latest search term from localStorage",
      extractedData: { latestSearchTerm },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

