import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input field to be visible
    await page.waitForSelector('#locationInput');

    // Type a search query into the search field
    const searchQuery = "New York";
    await page.fill('#locationInput', searchQuery);

    // Take a screenshot after entering the search query
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_query_entered.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_query_entered.png'));

    // Click on the search button (using a different selector strategy)
    await page.click('button.search-tab.active'); // Clicking on the active search tab, as a placeholder for the search button
    await page.waitForTimeout(2000); // Wait for search results to load (adjust as necessary)

    // Check localStorage for search history
    const searchHistory = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('searchHistory'));
    });

    // Extract the latest search term
    const latestSearchTerm = searchHistory ? searchHistory[searchHistory.length - 1] : null;

    // Take a final screenshot to capture the search results
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_results.png'));

    return {
      success: true,
      action: "Performed search and extracted latest search term",
      extractedData: { latestSearchTerm },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
