import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input to be visible and interact with it
    await page.waitForSelector('#locationInput');
    await page.fill('#locationInput', 'New York'); // Example search term
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_filled_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_filled_search.png'));

    // Click on the search button
    await page.waitForSelector('.search-btn');
    await page.click('.search-btn');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_performed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_performed.png'));

    // Verify if localStorage has been updated
    const searchHistory = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('searchHistory'));
    });

    // Extract the latest search term
    const latestSearchTerm = searchHistory ? searchHistory[searchHistory.length - 1] : null;

    return {
      success: true,
      action: "Performed search and extracted latest search term from localStorage",
      extractedData: { latestSearchTerm },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
