import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input field to be visible
    await page.waitForSelector('#locationInput');

    // Input a search term into the search field
    const searchTerm = 'New York';
    await page.fill('#locationInput', searchTerm);

    // Take a screenshot after entering the search term
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_term_entered.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_term_entered.png'));

    // Click on the search button
    await page.click('.search-btn');

    // Wait for the localStorage to update (you may need to adjust the wait time)
    await page.waitForTimeout(2000);

    // Extract the latest search term from localStorage
    const latestSearchTerm = await page.evaluate(() => {
      return localStorage.getItem('searchHistory'); // Adjust the key if necessary
    });

    // Take a screenshot after the search is performed
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_performed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_performed.png'));

    return {
      success: true,
      action: "Performed search and extracted latest search term from localStorage",
      extractedData: { latestSearchTerm },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

