import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the location input field to be available and interact with it
    await page.waitForSelector('#locationInput');
    await page.fill('#locationInput', 'New York'); // Example search query
    await page.press('#locationInput', 'Enter'); // Simulate pressing enter to search

    // Wait for search results to be loaded
    await page.waitForTimeout(2000); // Adjust as necessary for dynamic loading

    // Check localStorage for search history
    const searchHistory = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('searchHistory')) || [];
    });

    // Extract the latest search term
    const latestSearch = searchHistory.length > 0 ? searchHistory[searchHistory.length - 1] : null;

    // Take screenshot after performing search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_search.png'));

    return {
      success: true,
      action: "Performed search and extracted latest search term from localStorage",
      extractedData: { latestSearch },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
