import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input field to be visible
    await page.waitForSelector('#locationInput');

    // Input a search term
    const searchTerm = 'New York';
    await page.fill('#locationInput', searchTerm);

    // Take screenshot after entering search term
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_term_entered.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_term_entered.png'));

    // Click on the search button
    await page.click('.search-btn');

    // Wait for the search results to load
    await page.waitForSelector('.search-results', { timeout: 5000 });

    // Take screenshot of the search results
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_results.png'));

    // Extract the latest search term from localStorage
    const latestSearchTerm = await page.evaluate(() => {
      return localStorage.getItem('searchHistory') ? JSON.parse(localStorage.getItem('searchHistory')).slice(-1)[0] : null;
    });

    // Return the extracted data
    return {
      success: true,
      action: "Performed search and extracted latest search term from localStorage",
      extractedData: { latestSearchTerm },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Interact with the search input field
    const locationInput = await page.waitForSelector('#locationInput');
    await locationInput.type('New York', { delay: 100 });

    // Interact with the check-in and check-out date fields
    const checkinInput = await page.waitForSelector('#checkinInput');
    const checkoutInput = await page.waitForSelector('#checkoutInput');
    await checkinInput.fill('2023-10-15'); // Example check-in date
    await checkoutInput.fill('2023-10-20'); // Example check-out date

    // Click on the search button
    const searchButton = await page.waitForSelector('.search-btn');
    await searchButton.click();

    // Wait for localStorage to be updated and then retrieve search history
    await page.waitForTimeout(2000); // Wait for a while to ensure localStorage updates
    const searchHistory = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('searchHistory')) || [];
    });

    // Extract the latest search term
    const latestSearchTerm = searchHistory.length > 0 ? searchHistory[searchHistory.length - 1] : null;

    // Take a screenshot after the search is performed
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_performed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_performed.png'));

    return {
      success: true,
      action: "Searched for a location and verified localStorage update",
      extractedData: {
        latestSearchTerm
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

