import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input to be visible
    await page.waitForSelector('#locationInput', { state: 'visible' });

    // Fill search location
    await page.fill('#locationInput', 'Seoul');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_filled.png'));

    // Find and click search button using multiple selector strategies
    const searchBtn = await page.$('.search-btn') || 
                     await page.$('button:has-text("Search")') ||
                     await page.$('button[type="submit"]');

    if (searchBtn) {
      await searchBtn.click();
    } else {
      // Fallback: Press Enter in search field
      await page.press('#locationInput', 'Enter');
    }

    // Wait for search to complete
    await page.waitForLoadState('networkidle');
    await page.waitForTimeout(2000); // Additional wait for localStorage update

    // Extract search history from localStorage
    const searchHistory = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('searchHistory') || '[]');
    });

    // Get latest search term
    const latestSearch = searchHistory.length > 0 ? searchHistory[searchHistory.length - 1] : null;

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_complete.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_complete.png'));

    return {
      success: true,
      action: "Performed search and extracted latest search term from localStorage",
      extractedData: {
        searchHistory: searchHistory,
        latestSearch: latestSearch
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

