import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Fill search form
    await page.waitForSelector('#locationInput');
    await page.fill('#locationInput', 'Seoul');

    // Set check-in date (7 days from now)
    const checkinDate = new Date();
    checkinDate.setDate(checkinDate.getDate() + 7);
    await page.fill('#checkinInput', checkinDate.toISOString().split('T')[0]);

    // Set check-out date (14 days from now)
    const checkoutDate = new Date();
    checkoutDate.setDate(checkoutDate.getDate() + 14);
    await page.fill('#checkoutInput', checkoutDate.toISOString().split('T')[0]);

    // Open guest selector
    await page.click('.guest-selector');
    await page.waitForSelector('#guestMenu', { state: 'visible' });

    // Set guest counts
    await setGuestCount(page, 'adults', 2);
    await setGuestCount(page, 'children', 1);
    await setGuestCount(page, 'infants', 1);

    // Take screenshot before search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_form_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_form_filled.png'));

    // Perform search
    const searchBtn = await page.$x("//button[contains(@class, 'search-btn')]");
    await searchBtn[0].click();

    // Wait for search to complete
    await page.waitForTimeout(2000);
    await page.waitForLoadState('networkidle');

    // Take screenshot after search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_search.png'));

    // Extract search history from localStorage
    const searchHistory = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('airbnb_search_history') || '[]');
    });

    // Get latest search term
    const latestSearch = searchHistory.length > 0 ? searchHistory[searchHistory.length - 1] : null;

    return {
      success: true,
      action: "Performed search and extracted search history from localStorage",
      extractedData: {
        searchHistory: searchHistory,
        latestSearchTerm: latestSearch
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete search and extract search history",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

// Helper function to set guest counts
async function setGuestCount(page, type, targetCount) {
  const counterSelector = `#${type}`;
  let currentCount = parseInt(await page.textContent(counterSelector));

  while (currentCount !== targetCount) {
    if (currentCount < targetCount) {
      await page.click(`button[onclick="updateGuests('${type}', 1)"]`);
    } else {
      await page.click(`button[onclick="updateGuests('${type}', -1)"]`);
    }
    await page.waitForTimeout(100);
    currentCount = parseInt(await page.textContent(counterSelector));
  }
}
