import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search elements to load
    await page.waitForSelector('#locationInput');
    await page.waitForSelector('.search-btn');

    // Fill search location
    await page.fill('#locationInput', 'Seoul');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_filled.png'));

    // Perform search
    await page.click('.search-btn');
    await page.waitForTimeout(2000); // Wait for search to complete

    // Check localStorage for search history
    const searchHistory = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('searchHistory') || '[]');
    });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_search.png'));

    // Extract latest search term
    const latestSearch = searchHistory.length > 0 ? searchHistory[0] : null;

    return {
      success: true,
      action: "Performed search and extracted latest search term from localStorage",
      extractedData: {
        searchHistory: searchHistory,
        latestSearchTerm: latestSearch
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete search and localStorage verification",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input and fill with destination
    await page.waitForSelector('#locationInput');
    await page.fill('#locationInput', 'Seoul');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_location_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_location_filled.png'));

    // Set check-in date (tomorrow)
    const tomorrow = new Date();
    tomorrow.setDate(tomorrow.getDate() + 1);
    const checkinDate = tomorrow.toISOString().split('T')[0];
    await page.fill('#checkinInput', checkinDate);

    // Set check-out date (3 days from now)
    const checkoutDate = new Date();
    checkoutDate.setDate(checkoutDate.getDate() + 3);
    const checkoutDateStr = checkoutDate.toISOString().split('T')[0];
    await page.fill('#checkoutInput', checkoutDateStr);

    // Set guests to 2 adults
    await page.click('button.guest-selector');
    await page.waitForSelector('#guestMenu', { state: 'visible' });

    // Click + button twice for adults
    const adultPlusButton = await page.$('button:has-text("+"):nth-of-type(2)');
    await adultPlusButton.click();
    await adultPlusButton.click();

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_guests_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_guests_set.png'));

    // Find and click search button using multiple selector strategies
    const searchButton = await page.$('button.search-btn') || 
                         await page.$('button:has-text("Search")') ||
                         await page.$('button[type="submit"]');

    if (!searchButton) {
      throw new Error('Search button not found');
    }

    await searchButton.click();
    await page.waitForLoadState('networkidle');
    await page.waitForTimeout(2000); // Additional wait for localStorage update

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_after_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_after_search.png'));

    // Extract search history from localStorage
    const searchHistory = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('searchHistory') || '[]');
    });

    // Get latest search term
    const latestSearch = searchHistory.length > 0 ? searchHistory[searchHistory.length - 1] : null;

    return {
      success: true,
      action: "Performed search and extracted latest search term from localStorage",
      extractedData: {
        searchHistory: searchHistory,
        latestSearchTerm: latestSearch?.term || 'No search history found'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete search and extract search history",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Fill search form with test data
    await page.waitForSelector('#locationInput');
    await page.fill('#locationInput', 'Seoul');

    // Set check-in date (tomorrow)
    const tomorrow = new Date();
    tomorrow.setDate(tomorrow.getDate() + 1);
    const checkinDate = tomorrow.toISOString().split('T')[0];
    await page.fill('#checkinInput', checkinDate);

    // Set check-out date (2 days from now)
    const dayAfterTomorrow = new Date();
    dayAfterTomorrow.setDate(dayAfterTomorrow.getDate() + 2);
    const checkoutDate = dayAfterTomorrow.toISOString().split('T')[0];
    await page.fill('#checkoutInput', checkoutDate);

    // Set guests
    await page.click('button.guest-selector');
    await page.waitForSelector('#guestMenu');
    await page.click('button[onclick*="adults"][onclick*="1"]');

    // Click search button using multiple selector strategies
    const searchButton = await page.$('button.search-btn') || 
                        await page.$('button:has-text("Search")') ||
                        await page.$('button[type="submit"]');

    if (searchButton) {
      await searchButton.click();
    } else {
      // Fallback: press Enter in search field
      await page.press('#locationInput', 'Enter');
    }

    // Wait for search results to load
    await page.waitForTimeout(2000);
    await page.waitForLoadState('networkidle');

    // Take screenshot after search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_search.png'));

    // Extract search history from localStorage
    const searchHistory = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('airbnb_search_history') || '[]');
    });

    // Get latest search term
    const latestSearch = searchHistory.length > 0 ? searchHistory[searchHistory.length - 1] : null;

    return {
      success: true,
      action: "Performed search and extracted latest search term from localStorage",
      extractedData: {
        searchHistory: searchHistory,
        latestSearchTerm: latestSearch
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
