import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input and button to be visible
    await page.waitForSelector('#locationInput', { state: 'visible' });
    await page.waitForSelector('.search-btn', { state: 'visible' });

    // Fill search parameters
    await page.fill('#locationInput', 'Seoul');

    // Set check-in and check-out dates (7 days from today and 14 days from today)
    const today = new Date();
    const checkinDate = new Date(today);
    checkinDate.setDate(today.getDate() + 7);
    const checkoutDate = new Date(today);
    checkoutDate.setDate(today.getDate() + 14);

    await page.fill('#checkinInput', checkinDate.toISOString().split('T')[0]);
    await page.fill('#checkoutInput', checkoutDate.toISOString().split('T')[0]);

    // Take screenshot before search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_search.png'));

    // Click search button
    await page.click('.search-btn');
    await page.waitForLoadState('networkidle');
    await page.waitForTimeout(2000); // Additional wait for potential localStorage updates

    // Take screenshot after search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_search.png'));

    // Extract search history from localStorage
    const searchHistory = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('airbnb_search_history') || '[]');
    });

    // Get latest search term
    const latestSearch = searchHistory.length > 0 ? searchHistory[searchHistory.length - 1] : null;

    return {
      success: true,
      action: "Performed search and extracted latest search term from localStorage",
      extractedData: {
        searchHistory: searchHistory,
        latestSearch: latestSearch
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete search and extract localStorage data",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

