    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Fill search form
    await page.fill('#locationInput', 'Seoul');
    await page.waitForTimeout(1000);

    // Set check-in date (7 days from now)
    const checkinDate = new Date();
    checkinDate.setDate(checkinDate.getDate() + 7);
    const checkinFormatted = checkinDate.toISOString().split('T')[0];
    await page.fill('#checkinInput', checkinFormatted);

    // Set check-out date (14 days from now)
    const checkoutDate = new Date();
    checkoutDate.setDate(checkoutDate.getDate() + 14);
    const checkoutFormatted = checkoutDate.toISOString().split('T')[0];
    await page.fill('#checkoutInput', checkoutFormatted);

    // Set guest count
    await page.click('button.guest-selector');
    await page.waitForSelector('#guestMenu', { state: 'visible' });
    await page.click('button:has-text("+"):nth-of-type(1)');
    await page.click('button:has-text("+"):nth-of-type(1)');
    await page.click('button.guest-selector'); // Close guest menu

    // Take screenshot before search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_form.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_form.png'));

    // Click search button using multiple selector strategies
    const searchBtn = await page.$('button.search-btn') || 
                     await page.$('button:has-text("Search")') ||
                     await page.$('button[type="submit"]');
    await searchBtn.click();

    // Wait for search to complete
    await page.waitForTimeout(2000);
    await page.waitForLoadState('networkidle');

    // Take screenshot after search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_search.png'));

    // Extract search history from localStorage
    const searchHistory = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('searchHistory') || '[]');
    });

    // Get latest search term
    const latestSearch = searchHistory.length > 0 ? searchHistory[searchHistory.length - 1] : null;

    return {
      success: true,
      action: "Performed search and extracted search history from localStorage",
      extractedData: {
        searchHistory: searchHistory,
        latestSearchTerm: latestSearch
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete search and extract search history",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
