import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Fill search inputs
    await page.waitForSelector('#locationInput');
    await page.fill('#locationInput', 'Seoul, South Korea');

    await page.waitForSelector('#checkinInput');
    await page.fill('#checkinInput', '2024-12-01');

    await page.waitForSelector('#checkoutInput');
    await page.fill('#checkoutInput', '2024-12-07');

    // Select guests
    await page.click('.guest-selector');
    await page.waitForSelector('#guestMenu');
    await page.click('button:has-text("+")'); // Add adult
    await page.click('button:has-text("+")'); // Add another adult

    // Perform search
    await page.click('.search-btn');
    await page.waitForTimeout(2000); // Wait for search to complete

    // Take screenshot after search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_search.png'));

    // Extract search history from localStorage
    const searchHistory = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('searchHistory') || '[]');
    });

    // Get latest search term
    const latestSearch = searchHistory.length > 0 ? searchHistory[searchHistory.length - 1] : null;

    return {
      success: true,
      action: "Performed search and extracted latest search term from localStorage",
      extractedData: {
        searchHistory: searchHistory,
        latestSearchTerm: latestSearch?.query || 'No search history found'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input and fill with search term
    await page.waitForSelector('#locationInput', { timeout: 5000 });
    await page.fill('#locationInput', 'Seoul');

    // Set check-in and check-out dates (current date + 2 days and +7 days)
    const tomorrow = new Date();
    tomorrow.setDate(tomorrow.getDate() + 2);
    const nextWeek = new Date();
    nextWeek.setDate(nextWeek.getDate() + 7);

    await page.fill('#checkinInput', tomorrow.toISOString().split('T')[0]);
    await page.fill('#checkoutInput', nextWeek.toISOString().split('T')[0]);

    // Set guests to 2 adults
    await page.click('button.guest-selector');
    await page.waitForSelector('#guestMenu', { state: 'visible' });

    // Add 2 adults
    const adultPlusBtn = await page.$('xpath=//div[contains(@class, "guest-option")][1]//button[contains(text(), "+")]');
    await adultPlusBtn.click();
    await adultPlusBtn.click();

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_filled.png'));

    // Find and click search button using multiple selector strategies
    const searchBtn = await page.$('button.search-btn') || 
                     await page.$('xpath=//button[contains(@class, "search") and contains(text(), "Search")]') ||
                     await page.$('xpath=//button[contains(@class, "btn") and contains(text(), "Search")]');

    if (!searchBtn) {
      throw new Error('Search button not found');
    }

    await searchBtn.click();
    await page.waitForLoadState('networkidle');
    await page.waitForTimeout(2000); // Additional wait for localStorage update

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_search.png'));

    // Extract search history from localStorage
    const searchHistory = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('searchHistory') || '[]');
    });

    // Get latest search term
    const latestSearch = searchHistory.length > 0 ? searchHistory[searchHistory.length - 1] : null;

    return {
      success: true,
      action: "Performed search and extracted latest search term from localStorage",
      extractedData: {
        searchHistory: searchHistory,
        latestSearchTerm: latestSearch?.term || latestSearch
      },
      screenshots,
      error: null
    };
  } catch (error) {
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    return {
      success: false,
      action: "Failed to complete search and localStorage verification",
      extractedData: {},
      screenshots: [...screenshots, path.join(screenshotsDir, 'error.png')],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Fill search form
    await page.fill('#locationInput', 'Seoul');
    await page.fill('#checkinInput', '2024-01-15');
    await page.fill('#checkoutInput', '2024-01-20');

    // Set guests
    await page.click('button.guest-selector');
    await page.waitForSelector('.guest-menu', { state: 'visible' });

    // Add 2 adults
    await page.click('button[onclick*="updateGuests(\'adults\', 1)"]');
    await page.click('button[onclick*="updateGuests(\'adults\', 1)"]');

    // Take screenshot after filling form
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_form_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_form_filled.png'));

    // Find and click search button using multiple selector strategies
    const searchBtn = await page.$('button:has-text("Search")') || 
                     await page.$('[class*="search-btn"]') ||
                     await page.$('[class*="search-button"]');

    if (searchBtn) {
      await searchBtn.click();
    } else {
      // Fallback: Press Enter in location input
      await page.press('#locationInput', 'Enter');
    }

    // Wait for search to complete
    await page.waitForTimeout(2000);
    await page.waitForLoadState('networkidle');

    // Take screenshot after search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_search.png'));

    // Extract search history from localStorage
    const searchHistory = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('searchHistory') || '[]');
    });

    // Get latest search term
    const latestSearch = searchHistory.length > 0 ? searchHistory[searchHistory.length - 1] : null;

    return {
      success: true,
      action: "Performed search and extracted search history from localStorage",
      extractedData: {
        searchHistory: searchHistory,
        latestSearchTerm: latestSearch
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete search and extract localStorage data",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
