import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Fill search inputs
    await page.waitForSelector('#locationInput');
    await page.fill('#locationInput', 'Seoul');

    // Set check-in date (tomorrow)
    const tomorrow = new Date();
    tomorrow.setDate(tomorrow.getDate() + 1);
    const checkinDate = tomorrow.toISOString().split('T')[0];
    await page.fill('#checkinInput', checkinDate);

    // Set check-out date (2 days from now)
    const nextDay = new Date(tomorrow);
    nextDay.setDate(nextDay.getDate() + 2);
    const checkoutDate = nextDay.toISOString().split('T')[0];
    await page.fill('#checkoutInput', checkoutDate);

    // Set guests to 2 adults
    await page.click('button.guest-selector');
    await page.waitForSelector('#guestMenu', { state: 'visible' });

    const adultPlusButton = await page.$('button[onclick*="adults"][onclick*="1"]');
    await adultPlusButton.click();
    await adultPlusButton.click();

    // Take screenshot after filling form
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_form_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_form_filled.png'));

    // Find and click search button using XPath
    const searchButton = await page.$x('//button[contains(@class, "search-btn")]');
    await searchButton[0].click();

    // Wait for search to complete and localStorage to update
    await page.waitForTimeout(2000);

    // Extract search history from localStorage
    const searchHistory = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('airbnb_search_history') || '[]');
    });

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_complete.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_complete.png'));

    return {
      success: true,
      action: "Performed search and extracted search history from localStorage",
      extractedData: {
        latestSearch: searchHistory[searchHistory.length - 1],
        searchHistoryCount: searchHistory.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete search and extract localStorage data",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
