    const sortedPrices = [...prices].sort((a, b) => a - b);
    const isSorted = JSON.stringify(prices) === JSON.stringify(sortedPrices);

    // Take a screenshot after extracting prices
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_prices_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_prices_extracted.png'));

    return {
      success: true,
      action: "Extracted prices and verified order",
      extractedData: {
        prices,
        isSorted
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the price elements to load
    await page.waitForSelector('.listing-price .amount');

    // Extract prices from all listings
    const prices = await page.evaluate(() => {
      return Array.from(document.querySelectorAll('.listing-price .amount'), element => {
        return parseFloat(element.innerText.replace(/[^0-9.]/g, ''));
      });
    });

    // Validate if prices are sorted correctly
    const isSorted = prices.every((price, index) => index === 0 || price >= prices[index - 1]);

    // Log sorted prices for verification
    console.log('Extracted Prices:', prices);
    console.log('Are prices sorted?', isSorted);

    // Take screenshot of the prices
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_prices.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_prices.png'));

    return {
      success: true,
      action: "Successfully extracted and verified prices from listings",
      extractedData: {
        prices,
        isSorted
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
