import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the price elements to be visible
    await page.waitForSelector('.listing-price .amount');

    // Extract prices from the listings
    const prices = await page.$$eval('.listing-price .amount', elements => {
      return elements.map(el => parseFloat(el.innerText.replace(/[^0-9.-]+/g,""))); // Extract numeric values
    });

    // Take screenshot after prices are extracted
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_prices_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_prices_extracted.png'));

    // Validate if the prices are in ascending order
    const isSorted = prices.every((val, index, arr) => !index || val >= arr[index - 1]);

    return {
      success: true,
      action: "Extracted prices and validated sorting",
      extractedData: { prices, isSorted },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
