import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the listings to load
    await page.waitForSelector('.listing-price');

    // Extract prices from all listings
    const prices = await page.$$eval('.listing-price .amount', elements => 
      elements.map(el => parseFloat(el.innerText.replace(/[^0-9.-]+/g,"")))
    );

    // Verify if prices are sorted
    const isSorted = prices.every((val, i, arr) => !i || val >= arr[i - 1]);

    // Take screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_prices_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_prices_extracted.png'));

    return {
      success: true,
      action: "Extracted prices and verified sorting",
      extractedData: { prices, isSorted },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the accommodation prices to load
    await page.waitForSelector('.listing-price .amount', { timeout: 10000 });

    // Extract prices from the listings
    const prices = await page.$$eval('.listing-price .amount', elements => 
      elements.map(el => parseFloat(el.innerText.replace(/[^0-9.-]+/g,"")))
    );

    // Verify if the prices are in ascending order
    const isSorted = prices.slice().sort((a, b) => a - b).every((val, index) => val === prices[index]);

    // Take a screenshot of the prices
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_prices.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_prices.png'));

    return {
      success: true,
      action: "Extracted accommodation prices and verified order",
      extractedData: {
        prices,
        isSorted
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

