import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the price elements to load
    await page.waitForSelector('.listing-price .amount', { timeout: 5000 });

    // Extract prices from the elements
    const prices = await page.$$eval('.listing-price .amount', elements => 
      elements.map(element => parseFloat(element.innerText.replace(/[^0-9.]/g, '')))
    );

    // Validate the price order
    const isSorted = prices.every((price, index) => index === 0 || price >= prices[index - 1]);

    // Take a screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_prices_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_prices_extracted.png'));

    return {
      success: true,
      action: isSorted ? "Prices extracted and verified to be sorted correctly." : "Prices extracted but not sorted correctly.",
      extractedData: prices,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
