import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load
    await page.waitForSelector('.listing-card', { timeout: 10000 });

    // Extract all prices using multiple selector strategies
    const prices = await page.$$eval('.listing-price .amount', elements => 
      elements.map(el => {
        const priceText = el.textContent.trim();
        // Remove currency symbols and convert to number
        return parseFloat(priceText.replace(/[^\d.]/g, ''));
      })
    );

    // Verify if prices are in ascending order
    const isSorted = prices.every((price, index, array) => 
      index === 0 || price >= array[index - 1]
    );

    // Take screenshot of listings with prices
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_prices_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_prices_extracted.png'));

    return {
      success: true,
      action: "Extracted all listing prices and verified sort order",
      extractedData: {
        prices: prices,
        isSorted: isSorted,
        count: prices.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot if available
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
      screenshots.push(path.join(screenshotsDir, 'error.png'));
    } catch (e) {}

    return {
      success: false,
      action: "Failed to extract prices and verify sort order",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

