import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load and become visible
    await page.waitForSelector('.listing-card', { timeout: 10000 });

    // Scroll to ensure all listings are loaded (handle lazy loading)
    const listingCards = await page.$$('.listing-card');
    for (let i = 0; i < listingCards.length; i++) {
      await listingCards[i].scrollIntoViewIfNeeded();
      await page.waitForTimeout(500); // Small delay for loading
    }

    // Extract prices using multiple selector strategies
    const prices = await page.$$eval('.listing-price .amount', elements => 
      elements.map(el => {
        const priceText = el.textContent.trim();
        // Extract numeric value from price text (remove currency symbols, commas)
        return parseFloat(priceText.replace(/[^\d.]/g, ''));
      })
    );

    // Verify price order (ascending)
    const isAscending = prices.every((price, i, arr) => 
      i === 0 || price >= arr[i - 1]
    );

    // Take screenshot of listings
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_prices_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_prices_extracted.png'));

    return {
      success: true,
      action: "Extracted all accommodation prices and verified order",
      extractedData: {
        prices: prices,
        isPriceOrderCorrect: isAscending,
        totalListings: prices.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to extract prices and verify order",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
