import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load
    await page.waitForSelector('.listing-card', { timeout: 10000 });

    // Extract all listing prices
    const prices = await page.$$eval('.listing-price .amount', elements => 
      elements.map(el => {
        const priceText = el.textContent.trim();
        return parseInt(priceText.replace(/[^0-9]/g, ''));
      })
    );

    // Verify if prices are in ascending order
    let isAscending = true;
    for (let i = 0; i < prices.length - 1; i++) {
      if (prices[i] > prices[i + 1]) {
        isAscending = false;
        break;
      }
    }

    // Take screenshot of listings
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_prices_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_prices_extracted.png'));

    return {
      success: true,
      action: "Extracted all accommodation prices and verified order",
      extractedData: {
        prices: prices,
        isAscending: isAscending,
        count: prices.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load using multiple strategies
    await page.waitForSelector('.listing-card, [data-testid="listing-card"]', { timeout: 10000 });

    // Scroll to load all listings (if lazy loading)
    await page.evaluate(() => {
      window.scrollTo(0, document.body.scrollHeight);
    });

    await page.waitForTimeout(1000); // Allow time for loading

    // Take screenshot after loading listings
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_listings_loaded.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_listings_loaded.png'));

    // Extract prices using multiple selector strategies
    const prices = await page.$$eval(
      '.listing-price .amount, [class*="price"] span, [data-testid="listing-price"]',
      elements => elements.map(el => {
        const text = el.textContent.trim();
        // Clean price text (remove currency symbols, commas)
        return parseFloat(text.replace(/[^\d.]/g, ''));
      })
    );

    // Verify price order
    const isOrdered = prices.every((price, index, arr) => 
      index === 0 || price >= arr[index - 1]
    );

    // Take final screenshot with highlighted prices
    await page.evaluate(() => {
      document.querySelectorAll('.listing-price .amount, [class*="price"] span').forEach(el => {
        el.style.backgroundColor = 'yellow';
        el.style.border = '2px solid red';
      });
    });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_prices_highlighted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_prices_highlighted.png'));

    return {
      success: true,
      action: "Extracted all listing prices and verified ordering",
      extractedData: {
        prices: prices,
        isCorrectlyOrdered: isOrdered,
        totalListings: prices.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    const errorScreenshot = path.join(screenshotsDir, 'error.png');
    await page.screenshot({ path: errorScreenshot });

    return {
      success: false,
      action: "Failed to extract and verify listing prices",
      extractedData: {},
      screenshots: [errorScreenshot],
      error: error.message
    };
  }
}

