import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load
    await page.waitForSelector('.listing-card', { timeout: 10000 });

    // Extract all listing prices
    const prices = await page.$$eval('.listing-price .amount', elements => 
      elements.map(el => {
        const priceText = el.textContent.trim();
        return parseInt(priceText.replace(/[^\d]/g, ''), 10);
      })
    );

    // Take screenshot of listings
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_listings.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_listings.png'));

    // Verify if prices are in ascending order
    const isSorted = prices.every((price, index, array) => 
      index === 0 || price >= array[index - 1]
    );

    return {
      success: true,
      action: "Extracted all accommodation prices and verified order",
      extractedData: {
        prices,
        isSorted,
        totalListings: prices.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

