import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load using multiple selector strategies
    await page.waitForSelector('.listing-card', { timeout: 10000 });
    await page.waitForSelector('[data-testid="listing-card"]', { timeout: 10000 });

    // Scroll to ensure all listings are loaded (handles lazy loading)
    await page.evaluate(() => window.scrollTo(0, document.body.scrollHeight));
    await page.waitForTimeout(2000);

    // Extract prices using different selector strategies
    const prices = await page.$$eval(
      '.listing-price .amount, [class*="price"] .amount, [data-price]',
      elements => elements.map(el => {
        const text = el.textContent.trim();
        // Extract numeric value from price text
        const numericValue = parseFloat(text.replace(/[^\d.]/g, ''));
        return isNaN(numericValue) ? 0 : numericValue;
      })
    );

    // Take screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_prices_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_prices_extracted.png'));

    // Verify if prices are in correct order (ascending)
    let isSorted = true;
    for (let i = 1; i < prices.length; i++) {
      if (prices[i] < prices[i - 1]) {
        isSorted = false;
        break;
      }
    }

    return {
      success: true,
      action: "Extracted all accommodation prices and verified sorting order",
      extractedData: {
        prices: prices,
        isSorted: isSorted,
        totalListings: prices.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to extract prices and verify order",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
