import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load
    await page.waitForSelector('.listing-card', { timeout: 10000 });

    // Extract all listing prices
    const prices = await page.$$eval('.listing-price .amount', elements => 
      elements.map(el => {
        const priceText = el.textContent.trim();
        return parseFloat(priceText.replace(/[^\d.]/g, ''));
      })
    );

    // Take screenshot after price extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_prices_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_prices_extracted.png'));

    // Verify price order (ascending)
    let isSorted = true;
    for (let i = 1; i < prices.length; i++) {
      if (prices[i] < prices[i - 1]) {
        isSorted = false;
        break;
      }
    }

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_verification_complete.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_verification_complete.png'));

    return {
      success: true,
      action: "Extracted all listing prices and verified order",
      extractedData: {
        prices: prices,
        isSorted: isSorted,
        count: prices.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to extract prices and verify order",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load
    await page.waitForSelector('.listing-card', { timeout: 10000 });

    // Scroll to load all listings (if any lazy loading)
    await page.evaluate(() => {
      window.scrollTo(0, document.body.scrollHeight);
    });
    await page.waitForTimeout(2000);

    // Extract all prices using multiple selector strategies
    const prices = await page.$$eval(
      '.listing-price .amount, [data-testid="listing-price"], .price-amount, [class*="price"]',
      elements => elements.map(el => {
        const text = el.textContent.trim();
        // Extract numeric values from price text
        const numericValue = text.replace(/[^\d.]/g, '');
        return parseFloat(numericValue) || 0;
      })
    );

    // Verify price order (ascending)
    const isSorted = prices.every((price, index, array) => 
      index === 0 || price >= array[index - 1]
    );

    // Take screenshot of listings with prices
    await page.screenshot({ 
      path: path.join(screenshotsDir, 'step_2_prices_extracted.png'),
      fullPage: true 
    });
    screenshots.push(path.join(screenshotsDir, 'step_2_prices_extracted.png'));

    return {
      success: true,
      action: "Extracted all accommodation prices and verified order",
      extractedData: {
        prices: prices,
        isPriceOrderCorrect: isSorted,
        totalListings: prices.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ 
      path: path.join(screenshotsDir, 'error.png'),
      fullPage: true 
    });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to extract prices and verify order",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

