import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load and ensure they are visible
    await page.waitForSelector('.listing-card', { timeout: 10000 });

    // Scroll to load all listings (handle lazy loading)
    await page.evaluate(async () => {
      await new Promise((resolve) => {
        let totalHeight = 0;
        const distance = 100;
        const timer = setInterval(() => {
          const scrollHeight = document.body.scrollHeight;
          window.scrollBy(0, distance);
          totalHeight += distance;
          if(totalHeight >= scrollHeight){
            clearInterval(timer);
            resolve();
          }
        }, 100);
      });
    });

    // Wait for price elements to be visible
    await page.waitForSelector('.listing-price .amount', { timeout: 10000 });

    // Extract all prices using the specified selector
    const prices = await page.$$eval('.listing-price .amount', elements => 
      elements.map(el => {
        const priceText = el.textContent.trim();
        // Extract numeric value from price text (remove currency symbols, commas)
        return parseFloat(priceText.replace(/[^\d.]/g, ''));
      })
    );

    // Take screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_prices_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_prices_extracted.png'));

    // Verify if prices are in correct order (ascending)
    const isAscending = prices.every((price, i, arr) => 
      i === 0 || price >= arr[i - 1]
    );

    // Verify if prices are in correct order (descending)
    const isDescending = prices.every((price, i, arr) => 
      i === 0 || price <= arr[i - 1]
    );

    const isCorrectOrder = isAscending || isDescending;

    return {
      success: true,
      action: "Extracted all accommodation prices and verified ordering",
      extractedData: {
        prices: prices,
        isPriceOrderCorrect: isCorrectOrder,
        orderType: isAscending ? 'ascending' : (isDescending ? 'descending' : 'unordered')
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    const errorScreenshot = path.join(screenshotsDir, 'error.png');
    await page.screenshot({ path: errorScreenshot });
    screenshots.push(errorScreenshot);

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

