import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load
    await page.waitForSelector('.listing-card', { timeout: 10000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_listings_loaded.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_listings_loaded.png'));

    // Extract all prices using the specified selector
    const prices = await page.$$eval('.listing-price .amount', elements => 
      elements.map(el => {
        const text = el.textContent.trim();
        // Convert to number and remove any non-numeric characters except decimal point
        return parseFloat(text.replace(/[^\d.]/g, ''));
      })
    );

    // Take screenshot after price extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_prices_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_prices_extracted.png'));

    // Verify if prices are in correct order (ascending)
    let isSortedCorrectly = true;
    for (let i = 0; i < prices.length - 1; i++) {
      if (prices[i] > prices[i + 1]) {
        isSortedCorrectly = false;
        break;
      }
    }

    return {
      success: true,
      action: "Extracted all accommodation prices and verified order",
      extractedData: {
        prices: prices,
        isSortedCorrectly: isSortedCorrectly,
        totalListings: prices.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

