import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load
    await page.waitForSelector('.listing-card', { timeout: 10000 });

    // Extract all prices using multiple selector strategies
    const prices = await page.$$eval(
      '.listing-price .amount, [data-testid="listing-price"], .price-tag',
      elements => elements.map(el => {
        const priceText = el.textContent.trim();
        return parseInt(priceText.replace(/[^\d]/g, '')) || 0;
      })
    );

    // Verify if prices are in correct order (ascending)
    const isSorted = prices.every((price, index, arr) => 
      index === 0 || price >= arr[index - 1]
    );

    // Take screenshot of listings with prices
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_prices.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_prices.png'));

    return {
      success: true,
      action: "Extracted all accommodation prices and verified order",
      extractedData: {
        prices: prices,
        isPriceOrderCorrect: isSorted,
        totalListings: prices.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to extract prices and verify order",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
