import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load
    await page.waitForSelector('.listing-card', { timeout: 10000 });

    // Extract all listing prices
    const prices = await page.$$eval('.listing-price .amount', elements => 
      elements.map(el => {
        const priceText = el.textContent.trim();
        // Remove currency symbols and commas, convert to number
        return parseFloat(priceText.replace(/[^\d.]/g, ''));
      })
    );

    // Take screenshot after price extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_prices_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_prices_extracted.png'));

    // Verify if prices are in correct order (ascending)
    const isSorted = prices.every((price, i, arr) => 
      i === 0 || price >= arr[i - 1]
    );

    return {
      success: true,
      action: "Extracted all listing prices and verified order",
      extractedData: {
        prices: prices,
        isPriceOrderCorrect: isSorted
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to extract prices and verify order",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load and ensure we're on Stays tab
    await page.waitForSelector('.listing-card', { timeout: 10000 });

    // Scroll through listings to ensure all are loaded
    await page.evaluate(async () => {
      await new Promise((resolve) => {
        let totalHeight = 0;
        const distance = 500;
        const timer = setInterval(() => {
          const scrollHeight = document.body.scrollHeight;
          window.scrollBy(0, distance);
          totalHeight += distance;
          if (totalHeight >= scrollHeight) {
            clearInterval(timer);
            resolve();
          }
        }, 100);
      });
    });

    // Extract all prices using multiple selector strategies
    const prices = await page.$$eval(
      '[class*="price"], .listing-price, [data-testid*="price"], .amount',
      elements => elements.map(el => {
        const text = el.textContent.trim();
        // Extract numeric values from price strings
        const priceMatch = text.match(/\$?(\d+[,\d]*(\.\d{2})?)/);
        return priceMatch ? parseFloat(priceMatch[1].replace(/,/g, '')) : null;
      }).filter(price => price !== null)
    );

    // Verify price order
    const isSorted = prices.every((price, index, arr) => 
      index === 0 || price >= arr[index - 1]
    );

    // Take screenshot of listings
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_listings.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_listings.png'));

    return {
      success: true,
      action: "Extracted all accommodation prices and verified sorting order",
      extractedData: {
        prices: prices,
        isCorrectOrder: isSorted,
        totalListings: prices.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to extract and verify prices",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

