import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');
    await page.waitForTimeout(2000); // Additional wait for dynamic content

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load
    await page.waitForSelector('.listing-card', { timeout: 10000 });

    // Scroll to load all listings (handle lazy loading)
    await page.evaluate(async () => {
      await new Promise((resolve) => {
        let totalHeight = 0;
        const distance = 500;
        const timer = setInterval(() => {
          const scrollHeight = document.body.scrollHeight;
          window.scrollBy(0, distance);
          totalHeight += distance;
          if (totalHeight >= scrollHeight) {
            clearInterval(timer);
            resolve();
          }
        }, 100);
      });
    });

    // Take screenshot after scrolling
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_scroll.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_scroll.png'));

    // Extract listings with rating >= 4.8
    const highRatedListings = await page.$$eval('.listing-card', (listings) => {
      return listings
        .map(listing => {
          const ratingElement = listing.querySelector('.listing-rating span');
          const rating = ratingElement ? parseFloat(ratingElement.textContent) : 0;
          const id = listing.id || listing.getAttribute('data-id') || 'unknown-id';
          return { id, rating };
        })
        .filter(item => item.rating >= 4.8);
    });

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_final.png'));

    return {
      success: true,
      action: "Extracted high-rated listings (rating >= 4.8)",
      extractedData: highRatedListings,
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    const errorScreenshot = path.join(screenshotsDir, 'error.png');
    await page.screenshot({ path: errorScreenshot });

    return {
      success: false,
      action: "Failed to extract high-rated listings",
      extractedData: {},
      screenshots: [errorScreenshot],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load
    await page.waitForSelector('.listing-card', { timeout: 10000 });

    // Get all listings with ratings >= 4.8
    const highRatedListings = await page.evaluate(() => {
      const listings = [];

      // Find all listing elements
      const listingElements = document.querySelectorAll('.listing-card');

      for (const listing of listingElements) {
        // Try multiple selector strategies for rating
        const ratingElement = 
          listing.querySelector('.listing-rating span') ||
          listing.querySelector('[data-rating]') ||
          listing.querySelector('.rating-value');

        if (ratingElement) {
          const ratingText = ratingElement.textContent.trim();
          const rating = parseFloat(ratingText);

          if (!isNaN(rating) && rating >= 4.8) {
            // Try multiple ways to get listing ID
            const id = 
              listing.dataset.id ||
              listing.id ||
              listing.querySelector('a')?.href?.match(/listing_(\w+)/)?.[1];

            if (id) {
              listings.push({ id, rating });
            }
          }
        }
      }

      return listings;
    });

    // Take screenshot of results
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_results.png'));

    return {
      success: true,
      action: "Extracted high-rated listings with ratings >= 4.8",
      extractedData: highRatedListings,
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    const errorScreenshot = path.join(screenshotsDir, 'error.png');
    await page.screenshot({ path: errorScreenshot });

    return {
      success: false,
      action: "Failed to extract high-rated listings",
      extractedData: {},
      screenshots: [errorScreenshot],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load
    await page.waitForSelector('.listing-card, [data-testid="listing-card"]', { timeout: 10000 });

    // Get all listing elements
    const listings = await page.$$('.listing-card, [data-testid="listing-card"]');

    const results = [];

    // Process each listing
    for (const listing of listings) {
      try {
        // Extract rating using multiple selector strategies
        const ratingElement = await listing.$('.listing-rating span, [class*="rating"] span, [data-rating]');
        if (!ratingElement) continue;

        const ratingText = await ratingElement.textContent();
        const rating = parseFloat(ratingText);

        if (rating >= 4.8) {
          // Extract ID using multiple strategies
          const id = await listing.evaluate(el => 
            el.getAttribute('data-id') || 
            el.getAttribute('id') || 
            el.querySelector('[data-listing-id]')?.getAttribute('data-listing-id')
          );

          if (id) {
            results.push({ id, rating });
          }
        }
      } catch (err) {
        console.log('Error processing listing:', err);
      }
    }

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_results.png'));

    return {
      success: true,
      action: "Extracted listings with rating >= 4.8",
      extractedData: results,
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: [],
      screenshots,
      error: error.message
    };
  }
}
