import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click on the jun_1 listing
    await page.waitForSelector("[data-listing-id='jun_1']");
    await page.click("[data-listing-id='jun_1']");
    await page.waitForTimeout(1000);

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Wait for amenities to load and count them
    await page.waitForSelector('.amenity-tag');
    const amenitiesCount = await page.$$eval('.amenity-tag', elements => elements.length);

    await page.screenshot({ 
      path: path.join(screenshotsDir, 'step_3_amenities_visible.png'),
      fullPage: true 
    });
    screenshots.push(path.join(screenshotsDir, 'step_3_amenities_visible.png'));

    return {
      success: true,
      action: "Opened jun_1 listing modal and counted amenities",
      extractedData: { amenitiesCount },
      screenshots,
      error: null
    };
  } catch (error) {
    await page.screenshot({ 
      path: path.join(screenshotsDir, 'error_state.png'),
      fullPage: true 
    });
    screenshots.push(path.join(screenshotsDir, 'error_state.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Find and click the jun_1 listing using data attribute selector
    const jun1Listing = await page.waitForSelector('[data-listing-id="jun_1"]');
    await jun1Listing.click();

    // Wait for modal to appear
    await page.waitForSelector('.modal-content', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Find amenities section and count amenity tags
    const amenitiesSection = await page.waitForSelector('.amenities-section');
    const amenityTags = await amenitiesSection.$$eval('.amenity-tag', 
      elements => elements.length
    );

    await page.screenshot({ 
      path: path.join(screenshotsDir, 'step_3_amenities_counted.png'),
      clip: await amenitiesSection.boundingBox()
    });
    screenshots.push(path.join(screenshotsDir, 'step_3_amenities_counted.png'));

    return {
      success: true,
      action: "Opened jun_1 listing modal and counted amenities",
      extractedData: {
        amenitiesCount: amenityTags
      },
      screenshots,
      error: null
    };
  } catch (error) {
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [...screenshots, path.join(screenshotsDir, 'error.png')],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load and find jun_1 listing
    await page.waitForSelector('[data-listing-id]');
    const junListing = await page.$('[data-listing-id="jun_1"]');

    if (!junListing) {
      throw new Error('jun_1 listing not found');
    }

    // Click to open modal
    await junListing.click();
    await page.waitForTimeout(1000); // Wait for modal animation

    // Wait for modal to appear and take screenshot
    await page.waitForSelector('.modal-content', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Wait for amenities to load and count them
    await page.waitForSelector('.amenity-tag');
    const amenitiesCount = await page.$$eval('.amenity-tag', elements => elements.length);

    // Take final screenshot with amenities visible
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_amenities_visible.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_amenities_visible.png'));

    return {
      success: true,
      action: "Opened jun_1 listing modal and counted amenities",
      extractedData: {
        amenitiesCount: amenitiesCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
