import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click on jun_1 listing
    const jun1Selector = "[data-listing-id='jun_1']";
    await page.waitForSelector(jun1Selector, { timeout: 5000 });
    await page.click(jun1Selector);

    // Wait for modal to appear and take screenshot
    await page.waitForSelector('.modal-content', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Count amenities
    await page.waitForSelector('.amenity-tag', { timeout: 3000 });
    const amenitiesCount = await page.$$eval('.amenity-tag', elements => elements.length);

    // Take screenshot of amenities section
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_amenities_counted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_amenities_counted.png'));

    return {
      success: true,
      action: "Opened jun_1 modal and counted amenities",
      extractedData: {
        amenitiesCount: amenitiesCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

