import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click on the jun_1 listing
    const listingSelector = '[data-listing-id="jun_1"]';
    await page.waitForSelector(listingSelector, { timeout: 10000 });
    await page.click(listingSelector);

    // Wait for modal to appear and amenities to load
    await page.waitForSelector('.modal-content', { timeout: 15000 });
    await page.waitForSelector('.amenity-tag', { timeout: 10000 });

    // Take screenshot of opened modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Count amenities using multiple selector strategies
    const amenitiesCount = await page.$$eval('.amenity-tag', elements => elements.length);

    // Alternative count using different selector approach
    const xpathCount = await page.$$eval('//*[contains(@class, "amenity-tag")]', elements => elements.length);

    // Verify counts match
    if (amenitiesCount !== xpathCount) {
      throw new Error(`Amenity count mismatch: ${amenitiesCount} vs ${xpathCount}`);
    }

    return {
      success: true,
      action: "Opened jun_1 listing modal and counted amenities",
      extractedData: {
        amenitiesCount: amenitiesCount,
        verifiedCount: xpathCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    const errorScreenshot = path.join(screenshotsDir, 'error.png');
    await page.screenshot({ path: errorScreenshot });

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [errorScreenshot],
      error: error.message
    };
  }
}
