import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load and find jun_1 listing
    await page.waitForSelector('[data-listing-id="jun_1"]');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_listings_loaded.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_listings_loaded.png'));

    // Click on jun_1 listing to open modal
    await page.click('[data-listing-id="jun_1"]');

    // Wait for modal to open and amenities to load
    await page.waitForSelector('.amenity-tag', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_modal_opened.png'));

    // Count amenities
    const amenitiesCount = await page.$$eval('.amenity-tag', elements => elements.length);

    // Take final screenshot with amenities visible
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_amenities_counted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_amenities_counted.png'));

    return {
      success: true,
      action: "Opened jun_1 modal and counted amenities",
      extractedData: { amenitiesCount },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

