import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click on the jun_1 listing
    await page.waitForSelector('[data-listing-id="jun_1"]');
    await page.click('[data-listing-id="jun_1"]');

    // Wait for modal to appear and amenities to load
    await page.waitForSelector('.amenity-tag', { timeout: 10000 });

    // Take screenshot of the modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Count amenities
    const amenitiesCount = await page.$$eval('.amenity-tag', elements => elements.length);

    // Take screenshot showing amenities
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_amenities_count.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_amenities_count.png'));

    return {
      success: true,
      action: "Opened jun_1 listing modal and counted amenities",
      extractedData: { amenitiesCount },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to open jun_1 modal and count amenities",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load and find jun_1 listing using data attribute selector
    await page.waitForSelector('[data-listing-id="jun_1"]');
    const junListing = await page.$('[data-listing-id="jun_1"]');

    if (!junListing) {
      throw new Error('jun_1 listing not found');
    }

    // Click to open the modal
    await junListing.click();

    // Wait for modal to appear using XPath selector for modal content
    await page.waitForSelector('.modal-content', { timeout: 5000 });

    // Take screenshot of opened modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Wait for amenities to load and count them using class selector
    await page.waitForSelector('.amenity-tag');
    const amenitiesCount = await page.$$eval('.amenity-tag', elements => elements.length);

    // Take screenshot of amenities section
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_amenities.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_amenities.png'));

    return {
      success: true,
      action: "Opened jun_1 modal and counted amenities",
      extractedData: { amenitiesCount },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot if available
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
      screenshots.push(path.join(screenshotsDir, 'error.png'));
    } catch (e) {}

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Find and click on jun_1 listing using data attribute selector
    const jun1Selector = "[data-listing-id='jun_1']";
    await page.waitForSelector(jun1Selector);
    await page.click(jun1Selector);

    // Wait for modal to appear and take screenshot
    await page.waitForSelector('.modal-content', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Wait for amenities to load and count them
    await page.waitForSelector('.amenity-tag');
    const amenityCount = await page.$$eval('.amenity-tag', elements => elements.length);

    // Take final screenshot with amenities visible
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_amenities_counted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_amenities_counted.png'));

    return {
      success: true,
      action: "Opened jun_1 modal and counted amenities",
      extractedData: {
        amenityCount: amenityCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
