import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click on jun_1 listing
    await page.waitForSelector('[data-listing-id="jun_1"]');
    const jun1Listing = await page.$('[data-listing-id="jun_1"]');
    await jun1Listing.click();

    // Wait for modal to appear and take screenshot
    await page.waitForSelector('.modal-content', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Wait for amenities to load and count them
    await page.waitForSelector('.amenity-tag');
    const amenitiesCount = await page.$$eval('.amenity-tag', elements => elements.length);

    // Take screenshot of amenities section
    const amenitiesSection = await page.$('.amenities-container');
    if (amenitiesSection) {
      await amenitiesSection.screenshot({ path: path.join(screenshotsDir, 'step_3_amenities.png') });
      screenshots.push(path.join(screenshotsDir, 'step_3_amenities.png'));
    }

    return {
      success: true,
      action: "Opened jun_1 modal and counted amenities",
      extractedData: {
        amenitiesCount: amenitiesCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

