import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click on the jun_1 listing
    const jun1Selector = "[data-listing-id='jun_1']";
    await page.waitForSelector(jun1Selector, { timeout: 10000 });
    await page.click(jun1Selector);

    // Wait for modal to appear and take screenshot
    await page.waitForSelector('.modal', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Count amenities using multiple selector strategies
    const amenitiesCount = await page.evaluate(() => {
      // Try multiple selector strategies for robustness
      const selectors = [
        '.amenity-tag',
        '[class*="amenity"]',
        '[class*="facility"]',
        '.facility-tag'
      ];

      let count = 0;
      for (const selector of selectors) {
        const elements = document.querySelectorAll(selector);
        if (elements.length > 0) {
          count = elements.length;
          break;
        }
      }
      return count;
    });

    // Take final screenshot with amenities visible
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_amenities_visible.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_amenities_visible.png'));

    return {
      success: true,
      action: "Opened jun_1 modal and counted amenities",
      extractedData: {
        amenitiesCount: amenitiesCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
