import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open the modal for Jun's listing
    await page.click('text=Jun'); // Assuming there's a button or link with text 'Jun'
    await page.waitForSelector('#modal-checkin'); // Wait for the modal to appear

    // Input check-in date
    await page.fill('#checkinInput', '2023-10-01'); // Example check-in date
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_checkin_date.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_checkin_date.png'));

    // Input check-out date
    await page.fill('#checkoutInput', '2023-10-05'); // Example check-out date
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_checkout_date.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_checkout_date.png'));

    // Input number of guests
    await page.click('.guest-selector'); // Open guest selector
    await page.waitForSelector('#guestMenu'); // Wait for guest menu to appear
    await page.click('button:contains("+")'); // Increase the number of adults
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_guest_count.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_guest_count.png'));

    // Calculate total cost
    await page.click('text=Calculate'); // Assuming there's a button to calculate total cost
    await page.waitForSelector('#total-cost'); // Wait for total cost to be displayed

    // Extract total cost
    const totalCost = await page.textContent('#total-cost');

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_total_cost.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_total_cost.png'));

    return {
      success: true,
      action: "Opened Jun's modal, filled in reservation form, and checked total cost",
      extractedData: { totalCost },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

