import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open the booking modal
    await page.waitForSelector('button.guest-selector');
    await page.click('button.guest-selector');

    // Wait for the guest menu to appear
    await page.waitForSelector('#guestMenu');

    // Input check-in date
    await page.waitForSelector('#checkinInput');
    await page.fill('#checkinInput', '2023-12-01');

    // Input check-out date
    await page.waitForSelector('#checkoutInput');
    await page.fill('#checkoutInput', '2023-12-10');

    // Set the number of guests (adults)
    await page.click('button:contains("+")'); // Increment adults
    await page.click('button:contains("+")'); // Increment adults again

    // Wait for the total cost to update
    await page.waitForTimeout(1000); // Wait for total cost to be calculated

    // Extract total cost displayed
    const totalCostText = await page.locator('#total-cost').innerText();
    const totalCost = totalCostText ? totalCostText : 'Total cost not found';

    // Take a final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_final.png'));

    return {
      success: true,
      action: "Opened booking modal, filled out the form, and checked total cost.",
      extractedData: {
        totalCost
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
