import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open the modal for Jun's listing
    await page.click('button:contains("Stays")'); // Click on Stays tab
    await page.waitForSelector('#staysSearch'); // Wait for the search bar to load
    await page.click('button.guest-selector'); // Click to open guest selector
    await page.waitForSelector('#guestMenu'); // Wait for guest menu to appear

    // Input check-in and check-out dates
    await page.fill('#checkinInput', '2023-12-01'); // Set check-in date
    await page.fill('#checkoutInput', '2023-12-05'); // Set check-out date

    // Set number of guests
    await page.click('button:contains("+")'); // Increase adults count
    await page.click('button:contains("+")'); // Increase children count

    // Take a screenshot after filling the form
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_filled_form.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_filled_form.png'));

    // Calculate total cost
    await page.click('button:contains("Calculate")'); // Assuming there's a button to calculate total cost
    await page.waitForSelector('#total-cost'); // Wait for total cost to be displayed

    // Extract total cost
    const totalCost = await page.textContent('#total-cost');

    // Take a final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_total_cost.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_total_cost.png'));

    return {
      success: true,
      action: "Opened Jun's modal, filled in the reservation form, and calculated total cost.",
      extractedData: { totalCost },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

