import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open the jun_2 modal
    await page.click('button:contains("Add guests")');
    await page.waitForSelector('#guestMenu');

    // Input check-in date
    await page.fill('#checkinInput', '2023-10-01');

    // Input check-out date
    await page.fill('#checkoutInput', '2023-10-05');

    // Set number of guests (e.g., 2 adults, 1 child)
    await page.click('button:contains("+")'); // Increase adults
    await page.click('button:contains("+")'); // Increase adults
    await page.click('button:contains("+")'); // Increase children

    // Take screenshot after filling the form
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_form_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_form_filled.png'));

    // Calculate total cost
    await page.click('button:contains("Search")'); // Assuming there's a search button to calculate cost
    await page.waitForSelector('#total-cost'); // Wait for total cost to be displayed

    // Extract total cost
    const totalCost = await page.textContent('#total-cost');

    // Take final screenshot showing total cost
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_total_cost.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_total_cost.png'));

    return {
      success: true,
      action: "Opened jun_2 modal, filled reservation form, and checked total cost.",
      extractedData: { totalCost },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

