import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open the modal for Jun's listing
    await page.click('button:contains("Add guests")');
    await page.waitForSelector('#guestMenu');

    // Set check-in and check-out dates
    await page.fill('#checkinInput', '2023-12-01'); // Example check-in date
    await page.fill('#checkoutInput', '2023-12-05'); // Example check-out date

    // Set the number of guests
    await page.click('button:contains("+")'); // Increase adults
    await page.click('button:contains("+")'); // Increase children

    // Take a screenshot after filling the form
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_filled_form.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_filled_form.png'));

    // Calculate total cost
    await page.click('button:contains("Calculate")'); // Assuming there's a button to calculate total cost
    await page.waitForSelector('#total-cost');

    // Extract total cost
    const totalCost = await page.innerText('#total-cost');

    // Take a screenshot of the total cost
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_total_cost.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_total_cost.png'));

    return {
      success: true,
      action: "Opened Jun's modal, filled the reservation form, and calculated total cost.",
      extractedData: { totalCost },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

