import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open the modal for Jun's listing
    await page.click('text=Jun'); // Assuming there's a clickable element with Jun's name
    await page.waitForSelector('#modal-checkin'); // Wait for the modal to appear

    // Take screenshot of the modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Fill in the check-in date
    await page.fill('#checkinInput', '2023-12-01'); // Example check-in date
    await page.fill('#checkoutInput', '2023-12-10'); // Example check-out date

    // Fill in the number of guests
    await page.click('.guest-selector'); // Open guest selector
    await page.waitForSelector('#guestMenu'); // Wait for guest menu to appear
    await page.click('button:contains("+")'); // Increase adult count by 1
    await page.click('button:contains("+")'); // Increase children count by 1

    // Take screenshot after filling out the form
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_filled_form.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_filled_form.png'));

    // Calculate total cost (assuming there's a button to calculate)
    await page.click('text=Calculate Total'); // Example button to calculate total cost
    await page.waitForSelector('#total-cost'); // Wait for total cost to be displayed

    // Extract total cost
    const totalCost = await page.textContent('#total-cost');

    // Take screenshot of the total cost displayed
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_total_cost.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_total_cost.png'));

    return {
      success: true,
      action: "Opened Jun's modal, filled out the reservation form, and checked total cost",
      extractedData: { totalCost },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

