import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open the modal for booking
    await page.click('button.guest-selector');
    await page.waitForSelector('#guestMenu');

    // Input check-in date
    await page.fill('#checkinInput', '2023-12-01');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_checkin.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_checkin.png'));

    // Input check-out date
    await page.fill('#checkoutInput', '2023-12-05');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_checkout.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_checkout.png'));

    // Update guest count
    await page.click('button:contains("+")'); // Increment adults
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_guest_count.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_guest_count.png'));

    // Calculate total cost
    await page.click('button.search-btn'); // Assuming there's a search button to calculate cost
    await page.waitForSelector('#total-cost'); // Wait for total cost to be displayed

    // Extract total cost
    const totalCost = await page.textContent('#total-cost');

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_total_cost.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_total_cost.png'));

    return {
      success: true,
      action: "Opened booking modal, filled in dates and guest count, and verified total cost.",
      extractedData: { totalCost },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

