import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click on Jun's listing (jun_2)
    await page.waitForSelector('[data-listing-id="jun_2"]');
    await page.click('[data-listing-id="jun_2"]');

    // Wait for modal to appear and take screenshot
    await page.waitForSelector('.booking-form');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Set check-in date (tomorrow)
    const tomorrow = new Date();
    tomorrow.setDate(tomorrow.getDate() + 1);
    const checkinDate = tomorrow.toISOString().split('T')[0];
    await page.fill('#modal-checkin', checkinDate);

    // Set check-out date (3 days from now)
    const checkoutDate = new Date(tomorrow);
    checkoutDate.setDate(checkoutDate.getDate() + 3);
    const checkoutDateStr = checkoutDate.toISOString().split('T')[0];
    await page.fill('#modal-checkout', checkoutDateStr);

    // Set guests to 2 adults
    await page.click('#modal-guests');
    await page.waitForSelector('.guest-option');

    // Increase adult count to 2
    const adultPlusBtn = await page.$('div.guest-option:has-text("Adults") button:has-text("+")');
    await adultPlusBtn.click();
    await adultPlusBtn.click();

    // Close guest selector
    await page.click('#modal-checkin');

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_form_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_form_filled.png'));

    // Wait for and extract total cost
    await page.waitForSelector('#total-cost:not(:empty)');
    const totalCost = await page.textContent('#total-cost');

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_total_cost.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_total_cost.png'));

    return {
      success: true,
      action: "Opened jun_2 modal, filled booking form with dates and guests, and extracted total cost",
      extractedData: {
        checkinDate,
        checkoutDate: checkoutDateStr,
        guests: 2,
        totalCost: totalCost.trim()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click on jun_2 listing card using data attribute selector
    await page.waitForSelector('[data-listing-id="jun_2"]');
    await page.click('[data-listing-id="jun_2"]');

    // Wait for modal to appear and take screenshot
    await page.waitForSelector('.booking-form', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Fill check-in date (2 days from now)
    const checkInDate = new Date();
    checkInDate.setDate(checkInDate.getDate() + 2);
    const checkInStr = checkInDate.toISOString().split('T')[0];
    await page.fill('#modal-checkin', checkInStr);

    // Fill check-out date (5 days from now)
    const checkOutDate = new Date();
    checkOutDate.setDate(checkOutDate.getDate() + 5);
    const checkOutStr = checkOutDate.toISOString().split('T')[0];
    await page.fill('#modal-checkout', checkOutStr);

    // Set guest count to 2 adults
    await page.click('#modal-guests');
    await page.waitForSelector('.guest-option', { state: 'visible' });

    // Reset adults count to 0 first
    while (parseInt(await page.textContent('#adults')) > 0) {
      await page.click('button:has-text("-"):first-of-type');
    }

    // Add 2 adults
    for (let i = 0; i < 2; i++) {
      await page.click('button:has-text("+"):first-of-type');
      await page.waitForTimeout(300);
    }

    // Close guest selector
    await page.click('#modal-guests');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_form_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_form_filled.png'));

    // Wait for and extract total cost
    await page.waitForSelector('#total-cost', { state: 'visible' });
    const totalCost = await page.textContent('#total-cost');

    // Verify cost calculation
    const extractedData = {
      checkInDate: checkInStr,
      checkOutDate: checkOutStr,
      guests: 2,
      totalCost: totalCost.trim()
    };

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_final_result.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_final_result.png'));

    return {
      success: true,
      action: "Opened jun_2 modal, filled booking form with dates and guest count, and extracted total cost",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Find and click on Jun's listing (jun_2)
    const junListing = await page.waitForSelector('[data-id="jun_2"]');
    await junListing.click();
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Wait for booking form to load and fill dates
    await page.waitForSelector('.booking-form');

    // Set check-in date (tomorrow)
    const tomorrow = new Date();
    tomorrow.setDate(tomorrow.getDate() + 1);
    const checkinDate = tomorrow.toISOString().split('T')[0];
    await page.fill('#modal-checkin', checkinDate);

    // Set check-out date (3 days from now)
    const checkoutDate = new Date(tomorrow);
    checkoutDate.setDate(checkoutDate.getDate() + 3);
    const checkoutStr = checkoutDate.toISOString().split('T')[0];
    await page.fill('#modal-checkout', checkoutStr);

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_dates_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_dates_filled.png'));

    // Open guest selector and set guests
    await page.click('#modal-guests');
    await page.waitForSelector('.guest-menu', { state: 'visible' });

    // Set 2 adults
    const adultPlus = await page.$('xpath=//div[contains(@class, "guest-option")]//strong[text()="Adults"]/following-sibling::div//button[contains(text(), "+")]');
    await adultPlus.click();
    await adultPlus.click();

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_guests_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_guests_set.png'));

    // Close guest selector
    await page.click('body'); // Click outside to close

    // Wait for total cost calculation
    await page.waitForTimeout(2000);

    // Extract total cost
    const totalCost = await page.$eval('#total-cost', el => el.textContent.trim());

    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_total_calculated.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_total_calculated.png'));

    return {
      success: true,
      action: "Opened jun_2 modal, filled reservation form, and calculated total cost",
      extractedData: {
        checkinDate,
        checkoutDate: checkoutStr,
        guests: 2,
        totalCost
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
