import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click on jun_2 listing
    await page.waitForSelector('[data-id="jun_2"]');
    await page.click('[data-id="jun_2"]');

    // Wait for modal to appear
    await page.waitForSelector('.booking-form', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Fill check-in date
    await page.waitForSelector('#modal-checkin');
    await page.fill('#modal-checkin', '2024-06-15');

    // Fill check-out date
    await page.waitForSelector('#modal-checkout');
    await page.fill('#modal-checkout', '2024-06-20');

    // Set guest count
    await page.waitForSelector('#modal-guests');
    await page.click('#modal-guests');

    // Wait for guest dropdown and set 2 adults
    await page.waitForSelector('.guest-menu');
    const adultPlusBtn = await page.$('.guest-option:first-child .guest-counter button:last-child');
    await adultPlusBtn.click();
    await adultPlusBtn.click();

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_form_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_form_filled.png'));

    // Get total cost
    await page.waitForSelector('#total-cost');
    const totalCost = await page.$eval('#total-cost', el => el.textContent.trim());

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_total_cost.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_total_cost.png'));

    return {
      success: true,
      action: "Opened jun_2 modal, filled booking form with dates and guest count, and extracted total cost",
      extractedData: {
        checkinDate: '2024-06-15',
        checkoutDate: '2024-06-20',
        guestCount: 2,
        totalCost: totalCost
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click on Jun's listing (assuming jun_2 is visible on page)
    await page.waitForSelector('[data-id="jun_2"]');
    await page.click('[data-id="jun_2"]');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_listing_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_listing_clicked.png'));

    // Wait for modal to appear and booking form to be visible
    await page.waitForSelector('.booking-form', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_modal_opened.png'));

    // Fill check-in date
    await page.fill('#modal-checkin', '2024-06-15');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_checkin_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_checkin_filled.png'));

    // Fill check-out date
    await page.fill('#modal-checkout', '2024-06-20');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_checkout_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_checkout_filled.png'));

    // Set guest count by clicking plus button multiple times
    const guestPlusBtn = await page.$('#modal-guests + button:has-text("+")');
    for (let i = 0; i < 2; i++) {
        await guestPlusBtn.click();
        await page.waitForTimeout(300);
    }
    await page.screenshot({ path: path.join(screenshotsDir, 'step_6_guests_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_6_guests_set.png'));

    // Wait for total cost calculation and extract value
    await page.waitForTimeout(1000);
    const totalCost = await page.textContent('#total-cost');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_7_total_cost.png') });
    screenshots.push(path.join(screenshotsDir, 'step_7_total_cost.png'));

    return {
      success: true,
      action: "Opened jun_2 modal, filled booking form, and extracted total cost",
      extractedData: {
        totalCost: totalCost.trim()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click on the profile button to open menu
    await page.waitForSelector('button.profile-btn');
    await page.click('button.profile-btn');

    // Wait for profile dropdown and click on Host Dashboard
    await page.waitForSelector('.profile-dropdown a:has-text("Host Dashboard")');
    await page.click('.profile-dropdown a:has-text("Host Dashboard")');
    await page.waitForLoadState('networkidle');

    // Take screenshot after navigating to host dashboard
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_host_dashboard.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_host_dashboard.png'));

    // Wait for and click on jun_2 listing
    await page.waitForSelector('.listing-card:has-text("jun_2")');
    await page.click('.listing-card:has-text("jun_2")');
    await page.waitForLoadState('networkidle');

    // Take screenshot after selecting listing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_listing_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_listing_selected.png'));

    // Wait for and open booking modal
    await page.waitForSelector('.booking-form');
    await page.click('.reserve-btn');
    await page.waitForSelector('#modal-checkin', { state: 'visible' });

    // Take screenshot of opened modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_modal_opened.png'));

    // Fill check-in date
    await page.fill('#modal-checkin input[type="date"]', '2024-06-15');

    // Fill check-out date
    await page.fill('#modal-checkout input[type="date"]', '2024-06-20');

    // Open guests modal
    await page.click('#modal-guests button');
    await page.waitForSelector('.guest-counter', { state: 'visible' });

    // Set guests to 2 adults
    const plusButton = await page.$('xpath=//div[contains(@class, "guest-option") and contains(., "Adults")]//button[contains(text(), "+")]');
    await plusButton.click();
    await plusButton.click();

    // Close guests modal
    await page.click('.modal-close');

    // Take screenshot after filling form
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_form_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_form_filled.png'));

    // Extract total cost
    const totalCost = await page.$eval('#total-cost', el => el.textContent.trim());

    // Take final screenshot with results
    await page.screenshot({ path: path.join(screenshotsDir, 'step_6_final_result.png') });
    screenshots.push(path.join(screenshotsDir, 'step_6_final_result.png'));

    return {
      success: true,
      action: "Opened jun_2 modal, filled reservation form with dates and guest count, and extracted total cost",
      extractedData: {
        checkInDate: '2024-06-15',
        checkOutDate: '2024-06-20',
        guestCount: 2,
        totalCost: totalCost
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
