import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Find and click jun_2 listing to open modal
    await page.waitForSelector('[data-id="jun_2"]');
    await page.click('[data-id="jun_2"]');
    await page.waitForSelector('.booking-form');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Fill check-in date
    await page.waitForSelector('#modal-checkin');
    await page.fill('#modal-checkin', '2024-06-15');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_checkin_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_checkin_filled.png'));

    // Fill check-out date
    await page.waitForSelector('#modal-checkout');
    await page.fill('#modal-checkout', '2024-06-20');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_checkout_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_checkout_filled.png'));

    // Open guest selector and set guests
    await page.waitForSelector('#modal-guests');
    await page.click('#modal-guests');
    await page.waitForSelector('.guest-menu');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_guest_menu_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_guest_menu_opened.png'));

    // Set 2 adults
    await page.click('button:has-text("+") >> nth=0');
    await page.click('button:has-text("+") >> nth=0');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_6_guests_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_6_guests_set.png'));

    // Close guest menu
    await page.click('#modal-guests');
    await page.waitForTimeout(500);

    // Wait for total cost calculation and extract value
    await page.waitForSelector('#total-cost');
    const totalCost = await page.$eval('#total-cost', el => el.textContent.trim());
    await page.screenshot({ path: path.join(screenshotsDir, 'step_7_total_cost.png') });
    screenshots.push(path.join(screenshotsDir, 'step_7_total_cost.png'));

    return {
      success: true,
      action: "Opened jun_2 modal, filled check-in/check-out dates, set 2 guests, and verified total cost calculation",
      extractedData: {
        checkInDate: '2024-06-15',
        checkOutDate: '2024-06-20',
        numberOfGuests: 2,
        totalCost: totalCost
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search results to load and find jun_2 listing
    await page.waitForSelector('.listing-card');
    const jun2Listing = await page.$('div[data-id="jun_2"]');

    if (!jun2Listing) {
      throw new Error('jun_2 listing not found');
    }

    // Click on jun_2 listing to open modal
    await jun2Listing.click();
    await page.waitForSelector('#modal-checkin', { timeout: 10000 });

    // Take screenshot of opened modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Fill check-in date (7 days from today)
    const checkinDate = new Date();
    checkinDate.setDate(checkinDate.getDate() + 7);
    const checkinFormatted = checkinDate.toISOString().split('T')[0];
    await page.fill('#modal-checkin', checkinFormatted);

    // Fill check-out date (14 days from today)
    const checkoutDate = new Date();
    checkoutDate.setDate(checkoutDate.getDate() + 14);
    const checkoutFormatted = checkoutDate.toISOString().split('T')[0];
    await page.fill('#modal-checkout', checkoutFormatted);

    // Set guests to 2 adults
    await page.click('#modal-guests');
    await page.waitForSelector('.guest-menu');

    // Increase adults count to 2
    const plusButtons = await page.$$('.guest-counter button:has-text("+")');
    await plusButtons[0].click();
    await plusButtons[0].click();

    // Close guest menu by clicking outside
    await page.click('body');
    await page.waitForTimeout(500);

    // Wait for total cost to update and extract value
    await page.waitForSelector('#total-cost');
    const totalCost = await page.$eval('#total-cost', el => el.textContent.trim());

    // Take final screenshot with filled form
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_form_completed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_form_completed.png'));

    return {
      success: true,
      action: "Opened jun_2 modal, filled reservation form with dates and guests, and extracted total cost",
      extractedData: {
        checkin: checkinFormatted,
        checkout: checkoutFormatted,
        guests: 2,
        totalCost: totalCost
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on Jun's profile to access listings
    await page.waitForSelector('button.profile-btn');
    await page.click('button.profile-btn');

    // Wait for profile dropdown and click Profile link
    await page.waitForSelector('#profileDropdown', { state: 'visible' });
    await page.click('a:has-text("Profile")');
    await page.waitForLoadState('networkidle');

    // Wait for Jun's listings to load and click on jun_2
    await page.waitForSelector('.listing-card:has-text("Luxury Penthouse")');
    await page.click('.listing-card:has-text("Luxury Penthouse")');

    // Wait for modal to appear
    await page.waitForSelector('.booking-form', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Fill check-in date
    await page.waitForSelector('#modal-checkin');
    await page.fill('#modal-checkin', '2024-06-15');

    // Fill check-out date
    await page.waitForSelector('#modal-checkout');
    await page.fill('#modal-checkout', '2024-06-20');

    // Open guest selector
    await page.waitForSelector('#modal-guests');
    await page.click('#modal-guests');

    // Wait for guest menu and set guests
    await page.waitForSelector('.guest-menu', { state: 'visible' });
    await page.click('button:has-text("+"):nth(0)'); // Add 1 adult
    await page.click('button:has-text("+"):nth(0)'); // Add 2nd adult
    await page.click('button:has-text("+"):nth(2)'); // Add 1 child

    // Close guest menu by clicking outside
    await page.click('.booking-form');

    // Wait for cost calculation and capture total
    await page.waitForTimeout(2000); // Wait for calculation
    const totalCost = await page.textContent('#total-cost');

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_form_completed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_form_completed.png'));

    return {
      success: true,
      action: "Opened jun_2 modal, filled booking form with dates and guests, captured total cost",
      extractedData: {
        checkinDate: '2024-06-15',
        checkoutDate: '2024-06-20',
        adults: 2,
        children: 1,
        totalCost: totalCost
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
