import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click on jun_2 listing
    await page.waitForSelector('[data-id="jun_2"]', { timeout: 10000 });
    await page.click('[data-id="jun_2"]');

    // Wait for modal to appear and take screenshot
    await page.waitForSelector('.booking-form', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Set check-in date (tomorrow)
    const tomorrow = new Date();
    tomorrow.setDate(tomorrow.getDate() + 1);
    const checkinDate = tomorrow.toISOString().split('T')[0];
    await page.fill('#modal-checkin', checkinDate);

    // Set check-out date (3 days from now)
    const checkoutDate = new Date(tomorrow);
    checkoutDate.setDate(tomorrow.getDate() + 3);
    const checkoutDateStr = checkoutDate.toISOString().split('T')[0];
    await page.fill('#modal-checkout', checkoutDateStr);

    // Open guest selector
    await page.click('#modal-guests');
    await page.waitForSelector('.guest-option', { timeout: 3000 });

    // Set 2 adults
    const adultPlus = await page.$('div.guest-option:has-text("Adults") button:has-text("+")');
    await adultPlus.click();
    await adultPlus.click();

    // Close guest selector by clicking outside
    await page.click('.booking-form');

    // Wait for total cost to update and take screenshot
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_form_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_form_filled.png'));

    // Extract total cost
    const totalCost = await page.textContent('#total-cost');

    // Final verification screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_final_result.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_final_result.png'));

    return {
      success: true,
      action: "Opened jun_2 modal, filled booking form with dates and guests, and verified total cost",
      extractedData: {
        checkin: checkinDate,
        checkout: checkoutDateStr,
        guests: 2,
        totalCost: totalCost.trim()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

