import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click on Jun's listing (jun_2)
    await page.waitForSelector('.listing-card');
    const junListing = await page.$x("//div[contains(@class, 'listing-card') and contains(., 'Modern Apartment in Gangnam')]");
    await junListing[0].click();

    // Wait for modal to appear and take screenshot
    await page.waitForSelector('#modal-checkin');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Fill check-in date (2 days from today)
    const checkinDate = new Date();
    checkinDate.setDate(checkinDate.getDate() + 2);
    const checkinFormatted = checkinDate.toISOString().split('T')[0];
    await page.fill('#modal-checkin', checkinFormatted);

    // Fill check-out date (5 days from today)
    const checkoutDate = new Date();
    checkoutDate.setDate(checkoutDate.getDate() + 5);
    const checkoutFormatted = checkoutDate.toISOString().split('T')[0];
    await page.fill('#modal-checkout', checkoutFormatted);

    // Set guests to 2 adults
    await page.click('#modal-guests');
    await page.waitForSelector('.guest-option');

    // Increase adults count to 2
    const adultPlusBtn = await page.$x("//div[contains(@class, 'guest-option') and contains(., 'Adults')]//button[contains(., '+')]");
    await adultPlusBtn[0].click();
    await adultPlusBtn[0].click();

    // Take screenshot after form fill
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_form_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_form_filled.png'));

    // Wait for and extract total cost
    await page.waitForSelector('#total-cost');
    const totalCost = await page.$eval('#total-cost', el => el.textContent.trim());

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_total_calculated.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_total_calculated.png'));

    return {
      success: true,
      action: "Opened jun_2 modal, filled booking form with dates and guests, and extracted total cost",
      extractedData: {
        checkinDate: checkinFormatted,
        checkoutDate: checkoutFormatted,
        guests: 2,
        totalCost: totalCost
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete booking form automation",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

